// 受け取るデータのルール（型）を定義
type Props = {
  onStart: () => void; // 「何も返さない関数」という意味です
};

// Propsを受け取るように書き換えます
const StartPage = (props: Props) => {
  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>高市さんBot</h1>
      <p>なんでも聞いてください</p>

      {/* ボタンを押したら、親から渡された onStart を実行！ */}
      <button onClick={props.onStart} style={{ padding: '10px 20px' }}>
        チャットを始める
      </button>
    </div>
  );
};

export default StartPage;
